    var UserChooser = SCF.Model.extend({
        modelName: "UserChooserModel",

        getUserList: function(url, searchString, showUsers, showGroups, profilePath, request, response) {
            var loadingMsg = CQ.I18n.get("Loading") + "...";
            response([{
                avatarUrl: "",
                givenName: loadingMsg,
                familyName: "",
                loading: true
            }]);
            $.ajax({
                url: url,
                data: {
                    search: searchString,
                    profilePath: profilePath,
                    showUsers: showUsers,
                    showGroups: showGroups
                },
                type: "GET"
            }).success(function(data) {
                var jsonData = data.items ? data.items : jQuery.parseJSON(data);
                for (var user = 0; user < jsonData.length; user++) {
                    jsonData[user].profileURL = jsonData[user].profileURL + ".form.html" + (profilePath.trim().indexOf("/") == 0 ? profilePath.trim() : "/" + profilePath.trim());
                }
                response(jsonData);
            });
        },

        findUser: function(url, userId, showUsers, showGroups, profilePath) {
            var outObj = null;
            $.ajax({
                url: url,
                data: {
                    search: userId,
                    profilePath: profilePath,
                    showUsers: showUsers,
                    showGroups: showGroups
                },
                type: "GET",
                async: false
            }).success(function(data) {
                var jsonData = data.items ? data.items : jQuery.parseJSON(data);
                $.each(jsonData, function() {
                    this.profileURL = this.profileURL + ".form.html" + (profilePath.trim().indexOf("/") == 0 ? profilePath.trim() : "/" + profilePath.trim());
                    if (this.id.trim() == userId.trim()) {
                        outObj = this;
                    }
                });
            });
            return outObj;
        }
    });

    var UserChooserView = SCF.View.extend({
        view: "UserChooser",

        init: function() {
            Backbone.on('selectThisUser', this.selectUser, this);
        },

        events: {
            'click input.userchooser': function(e) {
                this.selectChooser(e);
            },
            'click .userchooserContainer .messaging-user-link': function(e) {
                this.checkAndDisableUserProfileLink(e);
            },
            'hover .userchooserContainer .messaging-user-link': function(e) {
                this.checkAndDisableUserProfileLink(e);
            },
            'click .inlineDiv .removeThisUser': function(e) {
                this.showUserChooser(e);
            }

        },

        checkAndDisableUserProfileLink: function(e) {
            if (!this.model.get("properties").profileFormPath) {
                $CQ(e.currentTarget).addClass('disabled-messaging-user-link');
                e.preventDefault();
            }
        },
        selectChooser: function(e) {
            var thisView = this;
            var properties = this.model.get("properties");
            var userchooserName = properties.userchooserName ? properties.userchooserName : "";
            var userChooserId = userchooserName + "Id";
            var currentPath = this.model.get("id") + ".social.json";
            var profilePath = "";
            if (properties.profilePath) {
                profilePath = properties.profilePath;
            }

            $("#" + userChooserId).autocomplete({
                minLength: 3,
                source: function(request, response) {
                    thisView.model.getUserList(currentPath, encodeURIComponent(thisView.$el.find("#" + userChooserId).val()),
                        properties.showUsers, properties.showGroups, profilePath, request, response);
                },
                select: function(event, ui) {
                    thisView.selectThisUser(userchooserName, this, ui.item.id, ui.item.name, ui.item.avatarUrl, ui.item.profileURL, true);
                    thisView.$el.find("#" + userChooserId).val('');
                    return false;
                }
            }).data("uiAutocomplete")._renderItem = function(ul, item) {
                if (item.avatarUrl) {
                    return $("<li></li>")
                        .append("<a><img src='" + item.avatarUrl + "' width='30' height='30'/>&nbsp;" + item.name + "</a>")
                        .data("item.autocomplete", item)
                        .appendTo(ul);
                } else {
                    return $("<li></li>")
                        .append("<a>" + item.name + "</a>")
                        .data("item.autocomplete", item)
                        .appendTo(ul);
                }
            };
        },
        replaceAll: function(find, replace, str) {
            return str.replace(new RegExp(find, 'g'), replace);
        },
        showUserChooser: function(e) {
            var properties = this.model.get("properties");
            var userchooserName = properties.userchooserName ? properties.userchooserName : "";
            var $target = this.$el.find(e.target);
            var currentList = this.model.get("currentSelectedUsers");
            var userId = $target.parent().find("[name='userId']").val();
            currentList = currentList.replace(userId + ";", "");
            this.model.set({
                "currentSelectedUsers": currentList
            });
            this.$el.find("#" + userchooserName + "Name").val(currentList);
            this.$el.find("#" + userchooserName + "Id").val('');
            $target.closest(".inlineDiv").remove();
        },
        selectUser: function(event) {
            this.selectThisUser(event.componentName,
                event.dispObject,
                event.id,
                event.name,
                event.avatarUrl,
                event.profileURL,
                event.readOnly);
        },
        selectThisUser: function(chooserName, dispObject, id, fullName, avatarUrl, profileURL, showClose) {
            var properties = this.model.get("properties");
            var profilePath = "";
            if (properties.profilePath) {
                profilePath = properties.profilePath;
            }
            var currentPath = this.model.get("id") + ".social.json";
            var userObj = null;
            if (fullName.indexOf("Loading") > -1) {
                return;
            }
            if (dispObject != null) {
                dispObject.style.visible = "false";
                dispObject.style.display = "none";
            }
            this.$el.find("#" + chooserName + "Id").show();
            var currentSelectedUsers = this.model.get("currentSelectedUsers");
            if (!currentSelectedUsers) {
                currentSelectedUsers = "";
            }
            currentSelectedUsers += id + ";";
            this.model.set({
                "currentSelectedUsers": currentSelectedUsers
            });
            this.$el.find("#" + chooserName + "Name").val(currentSelectedUsers);
            this.$el.find("#" + chooserName + "Value").show();
            var spanString = "<div class='inlineDiv'>";
            var profileString = fullName;
            var closeString = "";
            if (profileURL) {
                profileString = profileURL.trim() != "" ?
                    "<a href='" + profileURL + "' target='_blank' class='messaging-user-link'>" + fullName + "</a>" : fullName;
            } else {
                userObj = this.model.findUser(currentPath, id,
                    properties.showUsers, properties.showGroups, profilePath);
                if (userObj && (userObj.type == "user" || userObj.profileURL != null)) {
                    profileString = userObj.profileURL.trim() != "" ?
                        "<a href='" + userObj.profileURL + "' target='_blank' class='messaging-user-link'>" + fullName + "</a>" : fullName;
                }
            }
            if (avatarUrl) {
                profileString = "<img src='" + avatarUrl + "' width='30' height='30'/>&nbsp;&nbsp;" + profileString;
            } else {
                if (userObj && (userObj.type == "user" || userObj.avatarUrl != null)) {
                    profileString = "<img src='" + userObj.avatarUrl + "' width='30' height='30'/>&nbsp;&nbsp;" + profileString;
                }
            }
            if (showClose) {
                closeString = "&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<input name='userId' type='hidden' value='" + id + "'/><a href='#' class='removeThisUser'>X</a>";
            }
            this.$el.find("#" + chooserName + "Value").append(spanString + profileString + closeString + "&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</div>");
        }

    });

    SCF.UserChooserView = UserChooserView;
    SCF.UserChooser = UserChooser;

    SCF.registerComponent('social/messaging/components/hbs/userchooser', SCF.UserChooser, SCF.UserChooserView);


